<script>
  import amap from '../amap'

  let grandpa_house = [
    {id: 1, name: '民宅'},
    {id: 2, name: '酒店'}
  ];
  let father = [
    {pid: 1, id: 1, name: '普通民宅'},
    {pid: 1, id: 2, name: '原始洞穴'},
    {pid: 2, id: 3, name: '房车'},
    {pid: 2, id: 4, name: '精品酒店'},
    {pid: 2, id: 5, name: '特色酒店'},
  ];
  let son = [
    {grad: 1, id: 1, name: '民宅'},
    {grad: 1, id: 2, name: '民宅'},
    {grad: 2, id: 1, name: '酒店'}

  ];
  import basics_bar from './basics_bar'

  export default ({
    name: 'basics1',

    data() {
      return {
        checked: false,
        country: '中国',
        grandpa_house_select: '',
        grandpa_house_list: [],
        father_house_select: '',
        father_house_list: [],
        son_house_select: '',
        son_house_list: [],
        page: 1,
        value: '1',
        select_val: '',
        num1: '',
        num2: 0,
        num3: 0,
        textarea: '',
        state: '',
        state_array: [],
        location_array: [],
        city: '',
        plan: true,
      }
    },
    components: {
      basics_bar: basics_bar,
      amap: amap,
    },
    created: function () {
      this.$store.dispatch('is_login');
      this.$store.dispatch('get_independent');

      // this.location_array=this.$store.getters['get_valuation_location'];
      this.grandpa_house_list = grandpa_house;
      this.grandpa_house_select = '';
      // 默认的选项是父级的第一个参数

      var val = this.grandpa_house_select;
      this.father_house_list = father.filter(function (x) {
        return x.pid == val;
      })
      //  父级的数组已经根据爷爷的选项进行选择;

      this.father_house_select = this.father_house_list > 0 ? this.father_house_list[0].id : '';
      //  父级的默认选项已经选择完毕

      // val = this.father_house_select;
      this.son_house_list = son.filter(function (x) {
        return x.gard == val;
      })
      this.son_house_select = this.son_house_list.length > 0 ? this.son_house_list[0].id : '';
      //  儿子的默认值已经选择
      //*************************************************************
    },
    beforeCreate: function () {
    },
    watch: {
      grandpa_house_select: function (val) {
        let me = this;
        this.father_house_list = father.filter(function (x) {
          return x.pid == val;
        })
        this.father_house_select = this.father_house_list.length > 0 ? this.father_house_list[0].id : '';
        this.son_house_list = son.filter(function (x) {
          return x.grad == val;
        })
        this.son_house_select = this.son_house_list.length > 0 ? this.son_house_list[0].id : '';
        grandpa_house.forEach(function (item) {
          if (item.id == val) {
            me.new_house.genre = item.name;
          }
        });
        father.forEach(function (item) {
          if (item.id == me.father_house_select) {
            me.new_house.house_genre = item.name;
          }
        });
        son.forEach(function (item) {
          if (item.id == me.son_house_select) {
            me.new_house.bedroom_genre = item.name;
          }
        })
      },
      state: function (val) {
        let me = this;
        this.new_house.location.city = '';
        this.getState.forEach(function (item) {
          if (item.id == val) {
            me.new_house.location.state = item.value;
          }
        })
         this.getState.filter(function (item) {
          if (item.id == val) {
            me.new_house.location.state=item.name;
          }
        })
        this.location_array = this.location_.filter(function (item) {
          return item.parent_id == val;
        })
      },
      'new_house.location': {
        handler() {
          let me = this;
          for (let key in me.new_house.location) {
            if (this.new_house.location[key] == '') {
              me.plan = true;
              break;
            } else {
              me.plan = false;
            }
          }
        },
        deep: true,
      },
    },
    computed: {
      location_() {
        return this.$store.getters['get_valuation_location']
      },
      getState() {
        return this.$store.getters['getState'];
      },
      get_user() {
        return this.$store.getters['get_user'];
      },
      new_house() {
        return this.$store.getters['get_new_house'];
      }
    },

    methods: {
      page_add() {
        this.page++;
      },
      page_minus() {
        this.page--;
      },
      handleChange(val) {
        this.new_house.people_number = val;
      },
      bedChange(val) {
        this.new_house.bed_number = val;
      },
      roomChange(val) {
        this.new_house.house_number = val;
      },
      test: function () {
        console.log("111", 111);
      },
      page_add_() {
        this.new_house.user_id = this.get_user.id;
        console.log("this.new_house", this.new_house);
        this.page++;
      }
    },

  })
</script>


